 if (character != null && character.IsPlayingAction) return; //Do not try if the Character is doing an action if (!Has_Item) TryPickUp(); else TryDrop(); } public virtual void TryDrop() { if (!enabled) return; //Do nothing if this script is disabled if (item && !item.InCoolDown) { if (character != null && !character.IsPlayingAction /*&& Item.DropReaction != null*/) { Item.OnPreDropped.Invoke(gameObject); } Debugging(\\"Item Try Drop - \\" + Item.name); if (!item.ByAnimation) Invoke(nameof(DropItem), Item.DropDelay.Value); } } /// <summary> Tries the pickup logic checking all the correct conditions if the character does not have an item. </summary> public virtual void TryPickUp() { if (!isActiveAndEnabled) return; //Do nothing if this script is disabled if (FocusedItem && !FocusedItem.InCoolDown) { if (character != null && !character.IsPlayingAction) //Try Picking UP WHEN THE CHARACTER IS NOT MAKING ANY ANIMATION { if (FocusedItem.Align) { StartCoroutine(MTools.AlignLookAtTransform(transform.root, FocusedItem.transform, FocusedItem.AlignTime)); StartCoroutine(MTools.AlignTransformRadius(transform.root, FocusedItem.transform.position, FocusedItem.AlignTime, FocusedItem.AlignDistance)); } FocusedItem.OnPrePicked.Invoke(gameObject); //Do the On Picked First } Debugging(\\"Try Pick Up\\"); if (!FocusedItem.ByAnimation) Invoke(nameof(PickUpItem), FocusedItem.PickDelay.Value); } } /// <summary>Pick Up Logic. It can be called by the ANimator</summary> public void PickUpItem() { if (!isActiveAndEnabled) return; //Do nothing if this script is disabled if (Item == null) Item = FocusedItem; //Check for the Picked Item if (Item) { Debugging(\\"Item Picked - \\" + Item.name); if (Holder) { var localScale = Item.transform.localScale; Item.transform.parent = Holder; //Parent it to the Holder Item.transform.localPosition = PosOffset; //Offset the Position Item.transform.localEulerAngles = RotOffset; //Offset the Rotation Item.transform.localScale = localScale; //Offset the Rotation 