s; // We expect 9 elements of data per student int index = 0; while (std::getline(istr, token, ',') && index < tokens.size()) { tokens[index++] = token; } if (index == tokens.size()) { int daysArray[Student::daysArraySize] = { std::stoi(tokens[5]), std::stoi(tokens[6]), std::stoi(tokens[7]) }; DegreeProgram dp = DegreeProgram::UNKNOWN; if (tokens[8] == \\"SECURITY\\") dp = DegreeProgram::SECURITY; else if (tokens[8] == \\"NETWORK\\") dp = DegreeProgram::NETWORK; else if (tokens[8] == \\"SOFTWARE\\") dp = DegreeProgram::SOFTWARE; add(tokens[0], tokens[1], tokens[2], tokens[3], std::stoi(tokens[4]), daysArray, dp); } else { std::cerr << \\"Error parsing data: Expected 9 elements, found \\" << index << std::endl; } } void Roster::add(const std::string& studentID, const std::string& firstName, const std::string& lastName, const std::string& emailAddress, int age, const int daysInCourse1, const int daysInCourse2, const int daysInCourse3, DegreeProgram degreeProgram) { if (lastIndex < Roster::rosterSize - 1) { int daysArray[Student::daysArraySize] = {daysInCourse1, daysInCourse2, daysInCourse3}; classRosterArray[++lastIndex] = std::make_unique<Student>( studentID, firstName, lastName, emailAddress, age, daysArray, degreeProgram); } } void Roster::removeById(const std::string& studentID) { for (int i = 0; i <= lastIndex; ++i) { if (classRosterArray[i] && classRosterArray[i]->getStudentID() == studentID) { classRosterArray[i].reset(); // Release the memory and set the pointer to null std::cout << \\"Student with ID \\" << studentID << \\" has been removed.\\" << std::endl; return; } } std::cout << \\"Student ID not found.\\" << std::endl; } void Roster::printAll() const { for (int i = 0; i <= lastIndex; ++i) { if (classRosterArray[i]) classRosterArray[i]->print(); } } void Roster::printAverageDaysInCourse(const std::string& studentID) const { for (int i = 0; i <= lastIndex; ++i) { if (classRosterArray[i] && classRosterArray[i]->getStudentID() == studentID) { const auto& day